import { makeStyles } from '@material-ui/core/styles';

export const useStyles = makeStyles({
    wrapper: {
        borderRadius: '10px',
        boxShadow: '0 14px 28px rgba(0,0,0,0.25),0 10px 10px rgba(0,0,0,0.22)',
        position: 'relative',
        overflow: 'hidden',
        width: '768px',
        maxWidth: '100%',
        minHeight: '480px',
    },
    container: {
        position: 'absolute',
        top: 0,
        height: '100%',
        transition: 'all 0.6s ease-in-out'
    },
    signUp: {
        left: 0,
        width: '50%',
        opacity: 0,
        zIndex: 1,
    },
    signIn: {
        left: 0,
        width: '50%',
        zIndex: 2
    },
    overlayContainer: {

    }
});
